item.example:

BEGIN SCRIPT Jint
var a = 1 + 2;
var b = 'hello';
END SCRIPT -> VARS "a,b"




BEGIN SCRIPT CSharp Z.Expressions
var a = 10 * 10;
END SCRIPT -> VARS "a"




BEGIN SCRIPT CSharp StartMethod="Main"
public static int Main() {
  return 10 * 10;
}
END SCRIPT -> VARS "a"




BEGIN SCRIPT CSharp StartMethod="Main" ClassName="SilverBulletPro" NameSpace="SB" IsStatic=True
public static int Main() {
  return 10 * 10;
}
END SCRIPT -> VARS "a"




SET VAR "num" 10 Integer
BEGIN SCRIPT CSharp StartMethod="Main" ClassName="SilverBulletPro" NameSpace="SB" IsStatic=True "<num>"
public static int Main(int num) {
  return 10 * num;
}
END SCRIPT -> VARS "a"




BEGIN SCRIPT NodeJS <USER> <PASS>
module.exports = async (arg1, arg2) => {
    var out = arg1 + arg2;
    return "{\"out\": \"" + out + "\"}";
}
END SCRIPT -> VARS "out"
